  static: path.resolve(__dirname, '../static/lib'),  // dllÂ≠òÊîæË∑ØÂæÑ Áõ∏ÂØπ‰∫éÈ°πÁõÆÁöÑstatic/lib
  list: {  // Â§ö‰∏™ÂåÖÂàóË°®Áî®Êù•Âå∫ÂàÜ
	  vendor: ['vue/dist/vue.common.js','vue-router', 'vuex'],  
