module.exports = {
    context: __root, // ËøôÈáåÈÖçÁΩÆÁöÑcontextÂ∞±ÊòØÂêéÈù¢dllÁöÑcontext
    entry: config.dll.list,
    output: {
        path: config.dll.static,
        filename: '[name]-[chunkhash:7].dll.js',
        library: 'lib_[name]',
        // *** ËøôÈáå‰∏çË¶ÅÊ∑ªÂä†libraryTargetÔºåÂê¶ÂàôwebpackÊâìÂåÖÊó∂‰ºöÂá∫Èîô„ÄÇ
        // ÔºàÊèêÁ§∫ÊòØ__WEBPACK__EXTERNAL__MODULE__xxxÊú™ÂÆö‰πâÔºâ ***
        // libraryTarget: 'umd'
    },
    resolve: {
        modules: [path.resolve(__root, 'node_modules')],
        extensions: ['.js', '.json'],
        alias: {
            'vue$': 'vue/dist/vue.esm.js'
        }
    },
    // ËøôÈáåÊ≤°ÊúâÂÜôloadersÔºåÂ¶ÇÊûúÊúâÈúÄË¶ÅÂèØ‰ª•Ëá™Ë°åÊ∑ªÂä†loaders
    plugins: [
        // *** ËøôÈáåÂæàÂÖ≥ÈîÆ ***
        new webpack.DllPlugin({ // Âõ†‰∏∫‰∏äÈù¢ÂÜô‰∫ÜcontextÔºåÊâÄ‰ª•ËøôÈáåÂèØ‰ª•‰∏çÊåáÂÆöcontext
            // ËøôÈáåmanifestÁöÑÂêçÂ≠óÂøÖÈ°ªË¶ÅÊúâÂèòÈáèÔºåÂõ†‰∏∫Á±ª‰ºº‰∏äÈù¢ÁöÑcoreÂíåasset‰ºöÂàÜÂà´ÂàõÂª∫‰∏Ä‰∏™manifestÔºå
            // Â¶ÇÊûúÂêçÁß∞Áõ∏ÂêåÔºåmanifest‰ºöÁîüÊàê‰∏çËßÑËåÉÁöÑjsonÔºåÂú®ÂºïÁî®Êó∂‰ºöÊä•Èîô„ÄÇ
            path: path.resolve(__root, 'static/manifest/[name].manifest.json'),
            name: 'lib_[name]' // *** ËøôÈáåÁöÑÂêçÂ≠óÂøÖÈ°ª‰∏éoutput.library‰∏ÄËá¥ ***
        }),
