	new HTMLWebpackIncludeAssetsPlugin({
	    append: false,
	    publicPath: config.build.assetsPublicPath,
	    assets: [{
	        path: config.build.assetsSubDirectory,
	        glob: '**/*.js',
	        globPath: config.dll.static
        }],
	}),
